local base64 = require "base64"
local describe = require('ensure').describe

local dec = "Man is distinguished, not only by his reason, but by this singular passion from other animals, which is a lust of the mind, that by a perseverance of delight in the continued and indefatigable generation of knowledge, exceeds the short vehemence of any carnal pleasure."
local enc = ""

describe("base64", function()
	it("encodes properly", function(done)
		assert(base64.encode(dec) == enc)
		done()
	end)
	it("decodes properly", function(done)
		assert(base64.decode(enc) == dec)
		done()
	end)
end)
